import React from 'react';
import { Card, CardContent, CardMedia, Typography } from '@mui/material';

const CharacterCard = ({ character }) => {
  const cardStyle = {
    position: 'relative',
    minHeight: '100%',
    display: 'flex',
    flexDirection: 'column',
  };

  const imageStyle = {
    position: 'relative',
    width: '100%',
    height: '140px',
    objectFit: 'cover',
  };

  const nameContainerStyle = {
    position: 'absolute',
    top: '8px', // Adjust top position as needed
    left: '8px', // Adjust left position as needed
    width: 'calc(100% - 16px)', // Adjust width accounting for padding
    backgroundColor: 'rgba(0, 0, 0, 0.5)', // Translucent black background
    color: 'white', // Text color
    padding: '8px', // Padding around the text
    borderRadius: '5px', // Rounded corners
    textAlign: 'center',
  };

  const contentStyle = {
    marginTop: '140px', // Ensure content starts below the image
    padding: '16px', // Padding for content
  };

  return (
    <Card style={cardStyle}>
      <CardMedia
        component="img"
        alt={character.name}
        image={character.image}
        style={imageStyle}
      />
      <div style={nameContainerStyle}>
        <Typography variant="h6" component="div">
          {character.name}
        </Typography>
      </div>
      <CardContent style={contentStyle}>
        <Typography variant="body2" color="textSecondary" component="p">
          Status: {character.status}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Species: {character.species}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Gender: {character.gender}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Origin: {character.origin.name}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Last Location: {character.location.name}
        </Typography>
      </CardContent>
    </Card>
  );
};

export default CharacterCard;
