import React from 'react';
import { Card, CardContent, CardMedia, Typography } from '@mui/material';

const CharacterCard = ({ character }) => {
  const nameContainerStyle = {
    position: 'absolute',
    bottom: '0',
    left: '0',
    width: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.5)', // Translucent black background
    color: 'white', // Text color
    padding: '8px', // Padding around the text
    borderRadius: '0 0 5px 5px', // Rounded corners at the bottom
    textAlign: 'center',
  };

  const cardContentStyle = {
    position: 'relative',
    paddingBottom: '30px', // Ensure space for the name container
  };

  return (
    <Card>
      <CardMedia
        component="img"
        alt={character.name}
        height="140"
        image={character.image}
        title={character.name}
      />
      <CardContent style={cardContentStyle}>
        <div style={nameContainerStyle}>
          <Typography variant="h6" component="div">
            {character.name}
          </Typography>
        </div>
        <Typography variant="body2" color="textSecondary" component="p">
          Status: {character.status}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Species: {character.species}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Gender: {character.gender}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Origin: {character.origin.name}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Last Location: {character.location.name}
        </Typography>
      </CardContent>
    </Card>
  );
};

export default CharacterCard;
