import React from 'react';
import { Card, CardContent, CardMedia, Typography, makeStyles } from '@mui/material';

const useStyles = makeStyles({
  nameContainer: {
    backgroundColor: 'rgba(0, 0, 0, 0.5)', // translucent black background
    color: 'white', // white text color
    padding: '8px', // padding around the name
    borderRadius: '5px', // rounded corners
    textAlign: 'center', // center-align text
    position: 'absolute', // absolute positioning for overlay
    bottom: '10px', // 10px from the bottom
    left: '10px', // 10px from the left
    right: '10px', // 10px from the right
  },
  cardContent: {
    position: 'relative', // relative positioning for stacking context
  },
});

const CharacterCard = ({ character }) => {
  const classes = useStyles();

  return (
    <Card>
      <CardMedia
        component="img"
        alt={character.name}
        height="140"
        image={character.image}
        title={character.name}
      />
      <CardContent className={classes.cardContent}>
        <Typography gutterBottom variant="h5" component="div" className={classes.nameContainer}>
          {character.name}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Status: {character.status}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Species: {character.species}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Gender: {character.gender}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Origin: {character.origin.name}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Last Location: {character.location.name}
        </Typography>
      </CardContent>
    </Card>
  );
};

export default CharacterCard;
