import React from 'react';
import { Card, CardContent, CardMedia, Typography } from '@mui/material';

const CharacterCard = ({ character }) => {
  const cardStyle = {
    position: 'relative',
    display: 'flex',
    flexDirection: 'column',
    height: '100%',
    justifyContent: 'space-between', // Aligns items with space between them
  };

  const imageStyle = {
    width: '100%',
    height: '200px', // Adjust image height as needed
    objectFit: 'cover',
  };

  const nameContainerStyle = {
    position: 'absolute',
    top: 'auto',
    bottom: '16px', // Places name container above the details
    left: '0',
    width: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.5)', // Translucent black background
    color: 'white',
    padding: '8px',
    borderRadius: '5px',
    textAlign: 'center',
  };

  const contentStyle = {
    padding: '16px',
  };

  return (
    <Card style={cardStyle}>
      <CardMedia
        component="img"
        alt={character.name}
        image={character.image}
        style={imageStyle}
      />
      <CardContent style={contentStyle}>
        <Typography gutterBottom variant="h5" component="div">
          {character.name}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Status: {character.status}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Species: {character.species}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Gender: {character.gender}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Origin: {character.origin.name}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Last Location: {character.location.name}
        </Typography>
      </CardContent>
      <div style={nameContainerStyle}>
        <Typography variant="h6" component="div">
          {character.name}
        </Typography>
      </div>
    </Card>
  );
};

export default CharacterCard;
