import React, { useState, useEffect } from 'react';
import { fetchChar } from '../api/rickAndMortyApi';
import CharacterCard from './CardChar';
import './CharacterList.css';

const CharacterList = () => {
  const [char, setChar] = useState([]);
  const [term, setTerm] = useState('');
  const [filterSpecies, setFilterSpecies] = useState('');
  const [filterGender, setFilterGender] = useState('');
  const [filterOrigin, setFilterOrigin] = useState('');
  const [sort, setSort] = useState('asc');

  useEffect(() => {
    const getChar = async () => {
      const data = await fetchChar();
      setChar(data.results);
    };
    getChar();
  }, []);

  const search = (event) => {
    setTerm(event.target.value);
  };

  const handleSpecies = (event) => {
    setFilterSpecies(event.target.value);
  };

  const handleGender = (event) => {
    setFilterGender(event.target.value);
  };

  const handleOrigin = (event) => {
    setFilterOrigin(event.target.value);
  };

  const handleSortOrder = (event) => {
    setSort(event.target.value);
  };

  const filteredCharacters = characters.filter((character) => {
    return (
      character.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
      (filterSpecies === '' || character.species === filterSpecies) &&
      (filterGender === '' || character.gender === filterGender) &&
      (filterOrigin === '' || character.origin.name === filterOrigin)
    );
  }).sort((a, b) => {
    return sortOrder === 'asc' ? a.id - b.id : b.id - a.id;
  });

  return (
    <div className="character-list-container">
      <div className="filters">
        <div className="filter-section">
          <h4>Species</h4>
          <div>
            <label>
              <input
                type="radio"
                name="species"
                value=""
                checked={speciesFilter === ''}
                onChange={handleSpeciesChange}
              />{' '}
              All Species
            </label>
          </div>
          <div>
            <label>
              <input
                type="radio"
                name="species"
                value="Human"
                checked={speciesFilter === 'Human'}
                onChange={handleSpeciesChange}
              />{' '}
              Human
            </label>
          </div>
          <div>
            <label>
              <input
                type="radio"
                name="species"
                value="Alien"
                checked={speciesFilter === 'Alien'}
                onChange={handleSpeciesChange}
              />{' '}
              Alien
            </label>
          </div>
          <div>
            <label>
              <input
                type="radio"
                name="species"
                value="Other Species"
                checked={speciesFilter === 'Other Species'}
                onChange={handleSpeciesChange}
              />{' '}
              Other Species
            </label>
          </div>
        </div>
        <div className="filter-section">
          <h4>Gender</h4>
          <div>
            <label>
              <input
                type="radio"
                name="gender"
                value=""
                checked={genderFilter === ''}
                onChange={handleGenderChange}
              />{' '}
              All Genders
            </label>
          </div>
          <div>
            <label>
              <input
                type="radio"
                name="gender"
                value="Male"
                checked={genderFilter === 'Male'}
                onChange={handleGenderChange}
              />{' '}
              Male
            </label>
          </div>
          <div>
            <label>
              <input
                type="radio"
                name="gender"
                value="Female"
                checked={genderFilter === 'Female'}
                onChange={handleGenderChange}
              />{' '}
              Female
            </label>
          </div>
        </div>
        <div className="filter-section">
          <h4>Origin</h4>
          <div>
            <label>
              <input
                type="radio"
                name="origin"
                value=""
                checked={originFilter === ''}
                onChange={handleOriginChange}
              />{' '}
              All Origins
            </label>
          </div>
          <div>
            <label>
              <input
                type="radio"
                name="origin"
                value="unknown"
                checked={originFilter === 'unknown'}
                onChange={handleOriginChange}
              />{' '}
              Unknown
            </label>
          </div>
          <div>
            <label>
              <input
                type="radio"
                name="origin"
                value="Post-Apocalyptic Earth"
                checked={originFilter === 'Post-Apocalyptic Earth'}
                onChange={handleOriginChange}
              />{' '}
              Post-Apocalyptic Earth
            </label>
          </div>
          <div>
            <label>
              <input
                type="radio"
                name="origin"
                value="Nuptia 4"
                checked={originFilter === 'Nuptia 4'}
                onChange={handleOriginChange}
              />{' '}
              Nuptia 4
            </label>
          </div>
          <div>
            <label>
              <input
                type="radio"
                name="origin"
                value="Other Origins"
                checked={originFilter === 'Other Origins'}
                onChange={handleOriginChange}
              />{' '}
              Other Origins
            </label>
          </div>
        </div>
      </div>
      <div className="main-content">
        <div className="left-content">
          <div className="selected-filters">
            <h4>Selected Filters</h4>
            {speciesFilter && (
              <span className="filter-tag">
                {speciesFilter} <button onClick={() => setSpeciesFilter('')}>x</button>
              </span>
            )}
            {genderFilter && (
              <span className="filter-tag">
                {genderFilter} <button onClick={() => setGenderFilter('')}>x</button>
              </span>
            )}
            {originFilter && (
              <span className="filter-tag">
                {originFilter} <button onClick={() => setOriginFilter('')}>x</button>
              </span>
            )}
          </div>
          <div className="search-section">
            <input
              type="text"
              placeholder="Search by Name"
              value={searchTerm}
              onChange={handleSearch}
            />
          </div>
        </div>
        <div className="right-content">
          <div className="sort-section">
            <h4>Sort by ID</h4>
            <select onChange={handleSortOrderChange} value={sortOrder}>
              <option value="asc">Ascending</option>
              <option value="desc">Descending</option>
            </select>
          </div>
        </div>
        <div className="character-list">
          {filteredCharacters.map((character) => (
            <CharacterCard key={character.id} character={character} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default CharacterList;
