import React from 'react';
import { Card, CardContent, CardMedia, Typography, makeStyles } from '@mui/material';
import './CharacterCard.css'; // Import your custom CSS file

const useStyles = makeStyles({
  nameContainer: {
    position: 'relative',
    marginBottom: '10px',
    textAlign: 'center',
    '&::before': {
      content: '""',
      position: 'absolute',
      top: '0',
      left: '0',
      right: '0',
      bottom: '0',
      backgroundColor: 'rgba(0, 0, 0, 0.5)', // Translucent black background
      borderRadius: '5px',
      zIndex: '1', // Ensure it's above the image
    },
    '& > h5': {
      position: 'relative',
      zIndex: '2', // Ensure text is above the overlay
      color: 'white', // Text color
      padding: '8px', // Padding around the text
      borderRadius: '5px', // Rounded corners
    },
  },
});

const CharacterCard = ({ character }) => {
  const classes = useStyles();

  return (
    <Card>
      <CardMedia
        component="img"
        alt={character.name}
        height="140"
        image={character.image}
        title={character.name}
      />
      <CardContent>
        <div className={classes.nameContainer}>
          <Typography gutterBottom variant="h5" component="div">
            {character.name}
          </Typography>
        </div>
        <Typography variant="body2" color="textSecondary" component="p">
          Status: {character.status}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Species: {character.species}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Gender: {character.gender}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Origin: {character.origin.name}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Last Location: {character.location.name}
        </Typography>
      </CardContent>
    </Card>
  );
};

export default CharacterCard;
