import React, { useState } from 'react';
import { Container, Grid, TextField, FormControl, FormLabel, FormControlLabel, RadioGroup, Radio, Checkbox, Button, Select, MenuItem, InputLabel, Typography } from '@mui/material';
import { styled } from '@mui/system';
import backgroundImg from './bgimg.jpg';

const BackgroundContainer = styled('div')({
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  minHeight: '100vh',
  backgroundImage: `url(${backgroundImg})`,
  backgroundSize: 'cover',
  backgroundPosition: 'center',
});

const RegistrationForm = styled('form')({
  display: 'flex',
  flexDirection: 'column',
  gap: '20px',
  maxWidth: '600px',
  width: '100%',
  padding: '20px',
  backgroundColor: 'rgba(255, 255, 255, 0.9)',
  borderRadius: '10px',
});

const Registration = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    username: '',
    password: '',
    confirmPassword: '',
    email: '',
    phoneNumber: '',
    dateOfBirth: '',
    gender: '',
    hobbies: {
      reading: false,
      traveling: false,
      cooking: false,
      other: false,
    },
    profilePicture: null,
    country: '',
    bio: '',
    agreeToTerms: false,
  });

  const handleChange = (event) => {
    const { name, value, type, checked } = event.target;
    if (type === 'checkbox') {
      setFormData((prevData) => ({
        ...prevData,
        hobbies: {
          ...prevData.hobbies,
          [name]: checked,
        },
      }));
    } else if (type === 'file') {
      setFormData((prevData) => ({
        ...prevData,
        profilePicture: event.target.files[0],
      }));
    } else {
      setFormData((prevData) => ({
        ...prevData,
        [name]: value,
      }));
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Form Data:', formData);
    // You can add further logic here, like sending data to a server
  };

  return (
    <BackgroundContainer>
      <Container>
        <Grid container spacing={3}>
          {/* Left section with background image */}
          <Grid item md={6}>
            {/* Background image from styled component */}
          </Grid>
          {/* Right section with registration form */}
          <Grid item md={6}>
            <RegistrationForm id="registrationForm" onSubmit={handleSubmit}>
              <FormControl fullWidth>
                <FormLabel htmlFor="fullName" className="required">Full Name</FormLabel>
                <TextField
                  id="fullName"
                  name="fullName"
                  value={formData.fullName}
                  onChange={handleChange}
                  placeholder="Full Name"
                  variant="outlined"
                  required
                />
              </FormControl>
              {/* Rest of the form fields */}
              {/* ... */}
              <Grid container justifyContent="flex-end" spacing={2}>
                <Grid item>
                  <Button type="submit" variant="contained" color="primary">Submit</Button>
                </Grid>
                <Grid item>
                  <Button type="reset" variant="contained" color="error">Reset</Button>
                </Grid>
              </Grid>
            </RegistrationForm>
          </Grid>
        </Grid>
      </Container>
    </BackgroundContainer>
  );
};

export default Registration;
